<script type="text/javascript">
$().ready(function(){

	$(\'#pForm select[@name=type]\').change(function() {
		if (this.value == \'txt\')
			$(\'#extended\').hide();
		else
			$(\'#extended\').show();
	});


	$(\'#pForm\').submit(function() {

		var visible = new Array();
		if ($("select[@name=\'who\']", this).val() == \'cur\')
			visible = $(\'#grid\').getDataIDs()
			
		$(\'#ids\',this).val(visible.join());
		return true;
	});

});
</script>
'; ?>